
use strict;
use warnings;



use ExtUtils::MakeMaker 6.30;



my %WriteMakefileArgs = (
  "ABSTRACT" => "Transfer configuration generated by Netspoc to device.",
  "AUTHOR" => "Heinz Knutzen <heinz.knutzen\@dataport.de>",
  "BUILD_REQUIRES" => {
    "IPC::Run3" => 0,
    "Test::Differences" => 0,
    "Test::More" => 0
  },
  "CONFIGURE_REQUIRES" => {
    "ExtUtils::MakeMaker" => "6.30"
  },
  "DISTNAME" => "Netspoc-Approve",
  "EXE_FILES" => [
    "bin/compare-all",
    "bin/get-netspoc-approve-conf",
    "bin/newpolicy.pl",
    "bin/drc3.pl",
    "bin/missing-approve",
    "bin/start-jobs",
    "bin/approve.pl",
    "bin/compress-policies",
    "bin/delete-old-policies",
    "bin/approve-all"
  ],
  "LICENSE" => "gpl",
  "NAME" => "Netspoc::Approve",
  "PREREQ_PM" => {
    "Algorithm::Diff" => 0,
    "Carp" => 0,
    "Expect" => 0,
    "Exporter" => 0,
    "Fcntl" => 0,
    "File::Basename" => 0,
    "File::Temp" => 0,
    "Getopt::Long" => 0,
    "IO::Socket" => 0,
    "POSIX" => 0,
    "base" => 0,
    "strict" => 0,
    "warnings" => 0
  },
  "VERSION" => "1.082",
  "test" => {
    "TESTS" => "t/*.t"
  }
);


unless ( eval { ExtUtils::MakeMaker->VERSION(6.56) } ) {
  my $br = delete $WriteMakefileArgs{BUILD_REQUIRES};
  my $pp = $WriteMakefileArgs{PREREQ_PM};
  for my $mod ( keys %$br ) {
    if ( exists $pp->{$mod} ) {
      $pp->{$mod} = $br->{$mod} if $br->{$mod} > $pp->{$mod};
    }
    else {
      $pp->{$mod} = $br->{$mod};
    }
  }
}

delete $WriteMakefileArgs{CONFIGURE_REQUIRES}
  unless eval { ExtUtils::MakeMaker->VERSION(6.52) };

WriteMakefile(%WriteMakefileArgs);



